import anime from "../../node_modules/animejs/lib/anime.es.js";

function isInViewport(element) {
  const rect = element.getBoundingClientRect();

  const viewportHeight =
    window.innerHeight || document.documentElement.clientHeight;

  const elementTopIsVisible =
    rect.top <= viewportHeight && rect.top + rect.height >= 0;
  const elementBottomIsVisible =
    rect.bottom >= 0 && rect.bottom - rect.height <= viewportHeight;

  const elementLeftIsVisible =
    rect.left <= window.innerWidth && rect.left + rect.width >= 0;
  const elementRightIsVisible =
    rect.right >= 0 && rect.right - rect.width <= window.innerWidth;

  return (
    elementTopIsVisible &&
    elementBottomIsVisible &&
    elementLeftIsVisible &&
    elementRightIsVisible
  );
}

function animateCards() {
  const cards = document.querySelectorAll(".card");

  cards.forEach((card, index) => {
    if (isInViewport(card) && !card.classList.contains("animated")) {
      anime({
        targets: card,
        translateX: ["-100px", "0px"],
        opacity: [0, 1],
        easing: "easeOutExpo",
        duration: 1000,
        delay: index * 100,
      });
      card.classList.add("animated");
    }
  });
}

function animateContent() {
  const content = document.querySelectorAll(".content");

  content.forEach((content, index) => {
    if (isInViewport(content) && !content.classList.contains("animated")) {
      anime({
        targets: content,
        translateX: ["-100px", "0px"],
        opacity: [0, 1],
        easing: "easeOutExpo",
        duration: 1000,
        delay: index * 100,
      });
      content.classList.add("animated");
    }
  });
}

export { animateCards, animateContent };
